import React, { useState, useEffect } from 'react';
import { taskAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';

const MonthScreen = () => {
  const { systemDate } = useApp();
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentMonth, setCurrentMonth] = useState(new Date(systemDate));

  // Ê†ºÂºèÂåñÊó•Êúü‰∏∫YYYY-MM-DD
  const formatDate = (date) => {
    return date.toISOString().split('T')[0];
  };

  // Ëé∑ÂèñÊúàÂ∫¶‰ªªÂä°
  const fetchMonthTasks = async (month) => {
    try {
      setLoading(true);
      setError(null);

      const startDate = new Date(month.getFullYear(), month.getMonth(), 1);
      const endDate = new Date(month.getFullYear(), month.getMonth() + 1, 0);

      const response = await taskAPI.getTasks(
        formatDate(startDate), 
        formatDate(endDate)
      );

      if (response.success) {
        // ÂêéÁ´ØËøîÂõûÁöÑÊòØÊåâÊó•ÊúüÂàÜÁªÑÁöÑÂØπË±°ÔºåÈúÄË¶ÅËΩ¨Êç¢‰∏∫Âπ≥Èù¢Êï∞ÁªÑ
        const tasksByDate = response.data || {};
        const allTasks = [];

        Object.keys(tasksByDate).forEach(date => {
          const dateTasks = tasksByDate[date] || [];
          dateTasks.forEach(task => {
            allTasks.push({
              ...task,
              date: date // Ê∑ªÂä†Êó•ÊúüÂ≠óÊÆµ
            });
          });
        });

        setTasks(allTasks);
      } else {
        setError(response.message || 'Ëé∑Âèñ‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÁΩëÁªúÈîôËØØ');
    } finally {
      setLoading(false);
    }
  };

  // Ëé∑ÂèñÊåáÂÆöÊó•ÊúüÁöÑ‰ªªÂä°
  const getTasksForDate = (date) => {
    const dateStr = formatDate(date);
    const taskArray = Array.isArray(tasks) ? tasks : [];
    return taskArray.filter(task => task.date === dateStr);
  };

  // ÁîüÊàêÊó•ÂéÜÊï∞ÊçÆ
  const generateCalendarDays = () => {
    const year = currentMonth.getFullYear();
    const month = currentMonth.getMonth();

    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const startDate = new Date(firstDay);
    startDate.setDate(startDate.getDate() - firstDay.getDay()); // ‰ªéÂë®Êó•ÂºÄÂßã

    const days = [];
    const current = new Date(startDate);

    // ÁîüÊàê6Âë®ÁöÑÊó•ÊúüÔºà42Â§©Ôºâ
    for (let i = 0; i < 42; i++) {
      const dayTasks = getTasksForDate(current);
      const completedTasks = dayTasks.filter(task => task.completed).length;
      const totalTasks = dayTasks.length;

      days.push({
        date: new Date(current),
        dayNumber: current.getDate(),
        isCurrentMonth: current.getMonth() === month,
        isToday: formatDate(current) === formatDate(systemDate),
        tasks: dayTasks,
        completedTasks,
        totalTasks,
        completionRate: totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0
      });

      current.setDate(current.getDate() + 1);
    }

    return days;
  };

  // ‰∏ä‰∏Ä‰∏™Êúà
  const previousMonth = () => {
    setCurrentMonth(prev => new Date(prev.getFullYear(), prev.getMonth() - 1, 1));
  };

  // ‰∏ã‰∏Ä‰∏™Êúà
  const nextMonth = () => {
    setCurrentMonth(prev => new Date(prev.getFullYear(), prev.getMonth() + 1, 1));
  };

  // ÈÄâÊã©Êó•Êúü
  const selectDate = (date) => {
    setSelectedDate(date);
  };

  // ËÆ°ÁÆóÊúàÂ∫¶ÁªüËÆ°
  const getMonthStats = () => {
    const taskArray = Array.isArray(tasks) ? tasks : [];
    const monthTasks = taskArray.filter(task => {
      const taskDate = new Date(task.date);
      return taskDate.getMonth() === currentMonth.getMonth() && 
             taskDate.getFullYear() === currentMonth.getFullYear();
    });

    const completed = monthTasks.filter(task => task.completed).length;
    const total = monthTasks.length;
    const completionRate = total > 0 ? Math.round((completed / total) * 100) : 0;

    const totalStudyTime = monthTasks.reduce((acc, task) => {
      if (task.duration) {
        return acc + (task.duration.hour * 60) + task.duration.minute;
      }
      return acc;
    }, 0);

    return {
      total,
      completed,
      completionRate,
      totalStudyTime: Math.round(totalStudyTime / 60 * 10) / 10 // ËΩ¨Êç¢‰∏∫Â∞èÊó∂Ôºå‰øùÁïô1‰ΩçÂ∞èÊï∞
    };
  };

  useEffect(() => {
    fetchMonthTasks(currentMonth);
  }, [currentMonth]);

  const calendarDays = generateCalendarDays();
  const monthStats = getMonthStats();
  const weekDays = ['Êó•', '‰∏Ä', '‰∫å', '‰∏â', 'Âõõ', '‰∫î', 'ÂÖ≠'];

  if (loading) {
    return (
      <div data-testid="month-screen" className="p-6">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-2 text-gray-600">Âä†ËΩΩ‰∏≠...</p>
        </div>
      </div>
    );
  }

  return (
    <div data-testid="month-screen" className="p-6 max-w-7xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-800 mb-2">ÊúàÂ∫¶‰ªªÂä°</h1>
        <p className="text-gray-600">Êü•ÁúãÂíåÁÆ°ÁêÜÊØèÊúàÁöÑÂ≠¶‰π†‰ªªÂä°</p>
      </div>

      {error && (
        <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded-md">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Êó•ÂéÜËßÜÂõæ */}
        <div className="lg:col-span-3">
          <div className="bg-white rounded-lg shadow-md p-6">
            {/* Êó•ÂéÜÂ§¥ÈÉ® */}
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-gray-800">
                {currentMonth.getFullYear()}Âπ¥{currentMonth.getMonth() + 1}Êúà
              </h2>
              <div className="flex space-x-2">
                <button
                  onClick={previousMonth}
                  className="px-3 py-1 bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
                >
                  ‚Üê ‰∏äÊúà
                </button>
                <button
                  onClick={nextMonth}
                  className="px-3 py-1 bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
                >
                  ‰∏ãÊúà ‚Üí
                </button>
              </div>
            </div>

            {/* ÊòüÊúüÊ†áÈ¢ò */}
            <div className="grid grid-cols-7 gap-1 mb-2">
              {weekDays.map(day => (
                <div key={day} className="text-center text-sm font-medium text-gray-500 py-2">
                  {day}
                </div>
              ))}
            </div>

            {/* Êó•ÂéÜÁΩëÊ†º */}
            <div className="grid grid-cols-7 gap-1">
              {calendarDays.map((day, index) => (
                <CalendarDay
                  key={index}
                  day={day}
                  onSelect={selectDate}
                  isSelected={selectedDate && formatDate(selectedDate) === formatDate(day.date)}
                />
              ))}
            </div>
          </div>
        </div>

        {/* ‰æßËæπÊ†è */}
        <div className="space-y-6">
          {/* ÊúàÂ∫¶ÁªüËÆ° */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Êú¨ÊúàÁªüËÆ°</h3>
            <div className="space-y-4">
              <div className="text-center">
                <div className="text-3xl font-bold text-blue-600 mb-1">
                  {monthStats.completionRate}%
                </div>
                <div className="text-sm text-gray-600">ÂÆåÊàêÁéá</div>
              </div>

              <div className="space-y-3 text-sm">
                <div className="flex justify-between">
                  <span className="text-gray-600">ÊÄª‰ªªÂä°Êï∞</span>
                  <span className="font-semibold">{monthStats.total}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Â∑≤ÂÆåÊàê</span>
                  <span className="font-semibold text-green-600">{monthStats.completed}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Êú™ÂÆåÊàê</span>
                  <span className="font-semibold text-red-600">
                    {monthStats.total - monthStats.completed}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Â≠¶‰π†Êó∂Èïø</span>
                  <span className="font-semibold">{monthStats.totalStudyTime}h</span>
                </div>
              </div>
            </div>
          </div>

          {/* ÈÄâ‰∏≠Êó•ÊúüÁöÑ‰ªªÂä° */}
          {selectedDate && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-lg font-semibold text-gray-800 mb-4">
                {selectedDate.getMonth() + 1}Êúà{selectedDate.getDate()}Êó•‰ªªÂä°
              </h3>

              {getTasksForDate(selectedDate).length === 0 ? (
                <p className="text-gray-500 text-sm">ÂΩìÊó•Êó†‰ªªÂä°</p>
              ) : (
                <div className="space-y-3">
                  {getTasksForDate(selectedDate).map(task => (
                    <div
                      key={task.id}
                      className={`p-3 rounded border ${
                        task.completed 
                          ? 'bg-green-50 border-green-200' 
                          : 'bg-gray-50 border-gray-200'
                      }`}
                    >
                      <div className="flex items-center space-x-2">
                        <div className={`w-3 h-3 rounded-full ${
                          task.completed ? 'bg-green-500' : 'bg-gray-400'
                        }`} />
                        <span className={`text-sm font-medium ${
                          task.completed ? 'text-green-700' : 'text-gray-700'
                        }`}>
                          {task.title || '‰ªªÂä°Ê†áÈ¢ò'}
                        </span>
                      </div>
                      {task.duration && (task.duration.hour > 0 || task.duration.minute > 0) && (
                        <div className="mt-1 text-xs text-gray-500 ml-5">
                          Áî®Êó∂: {task.duration.hour}Êó∂{task.duration.minute}ÂàÜ
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Áä∂ÊÄÅÂõæ‰æã */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Áä∂ÊÄÅËØ¥Êòé</h3>
            <div className="space-y-3 text-sm">
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-green-100 border border-green-200 rounded"></div>
                <span>‰ªªÂä°ÂÖ®ÈÉ®ÂÆåÊàê</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-yellow-100 border border-yellow-200 rounded"></div>
                <span>ÈÉ®ÂàÜ‰ªªÂä°ÂÆåÊàê</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-red-100 border border-red-200 rounded"></div>
                <span>‰ªªÂä°Êú™ÂÆåÊàê</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-yellow-50 border border-yellow-300 rounded"></div>
                <span>üè† Â∑≤ËØ∑ÂÅá</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-blue-50 border border-blue-300 rounded"></div>
                <span>üò¥ ‰ºëÊÅØÊó•</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 bg-gray-50 border border-gray-200 rounded"></div>
                <span>Êó†‰ªªÂä°ÂÆâÊéí</span>
              </div>
            </div>
          </div>

          {/* Âø´ÈÄüÂØºËà™ */}
          <div className="bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold text-gray-800 mb-4">Âø´ÈÄüÂØºËà™</h3>
            <div className="space-y-2">
              <button
                onClick={() => {
                  setCurrentMonth(new Date(systemDate));
                  setSelectedDate(systemDate);
                }}
                className="w-full px-3 py-2 text-left text-sm bg-blue-50 text-blue-700 rounded hover:bg-blue-100"
              >
                ÂõûÂà∞‰ªäÊó•
              </button>
              <button
                onClick={() => setCurrentMonth(new Date())}
                className="w-full px-3 py-2 text-left text-sm bg-gray-50 text-gray-700 rounded hover:bg-gray-100"
              >
                ÂΩìÂâçÊúà‰ªΩ
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Êó•ÂéÜÂçïÂÖÉÊ†ºÁªÑ‰ª∂
const CalendarDay = ({ day, onSelect, isSelected }) => {
  // Ê£ÄÊü•ÊòØÂê¶ÊúâËØ∑ÂÅáÊàñ‰ºëÊÅØ‰ªªÂä°
  const hasLeaveTask = day.tasks.some(task => task.type === 'leave');
  const hasRestTask = day.tasks.some(task => task.type === '‰ºëÊÅØ');

  const getCompletionColor = () => {
    if (hasLeaveTask) return 'bg-yellow-50'; // ËØ∑ÂÅáÊó•
    if (hasRestTask) return 'bg-blue-50'; // ‰ºëÊÅØÊó•
    if (day.totalTasks === 0) return 'bg-gray-50';
    if (day.completionRate === 100) return 'bg-green-100';
    if (day.completionRate >= 50) return 'bg-yellow-100';
    return 'bg-red-100';
  };

  const getBorderColor = () => {
    if (isSelected) return 'border-blue-500 border-2';
    if (day.isToday) return 'border-blue-300 border-2';
    if (hasLeaveTask) return 'border-yellow-300';
    if (hasRestTask) return 'border-blue-300';
    return 'border-gray-200';
  };

  const getStatusIcon = () => {
    if (hasLeaveTask) return 'üè†'; // ËØ∑ÂÅá
    if (hasRestTask) return 'üò¥'; // ‰ºëÊÅØ
    return null;
  };

  return (
    <div
      onClick={() => onSelect(day.date)}
      className={`
        relative h-24 p-1 border cursor-pointer hover:bg-gray-50 transition-colors
        ${getBorderColor()}
        ${getCompletionColor()}
        ${!day.isCurrentMonth ? 'opacity-30' : ''}
      `}
    >
      <div className="flex justify-between items-start h-full">
        <div className="flex items-center space-x-1">
          <span className={`text-sm font-medium ${
            day.isToday ? 'text-blue-600' : 
            day.isCurrentMonth ? 'text-gray-900' : 'text-gray-400'
          }`}>
            {day.dayNumber}
          </span>
          {getStatusIcon() && (
            <span className="text-xs">{getStatusIcon()}</span>
          )}
        </div>

        {(day.totalTasks > 0 && !hasLeaveTask && !hasRestTask) && (
          <div className="text-right">
            <div className="text-xs text-gray-600">
              {day.completedTasks}/{day.totalTasks}
            </div>
            {day.completionRate > 0 && (
              <div className={`text-xs font-semibold ${
                day.completionRate === 100 ? 'text-green-600' :
                day.completionRate >= 50 ? 'text-yellow-600' : 'text-red-600'
              }`}>
                {day.completionRate}%
              </div>
            )}
          </div>
        )}

        {/* ÁâπÊÆäÁä∂ÊÄÅÊòæÁ§∫ */}
        {hasLeaveTask && (
          <div className="text-right">
            <div className="text-xs text-yellow-600 font-semibold">Â∑≤ËØ∑ÂÅá</div>
          </div>
        )}

        {hasRestTask && !hasLeaveTask && (
          <div className="text-right">
            <div className="text-xs text-blue-600 font-semibold">‰ºëÊÅØÊó•</div>
          </div>
        )}
      </div>

      {/* ‰ªªÂä°ÊåáÁ§∫Âô® */}
      {day.totalTasks > 0 && (
        <div className="absolute bottom-1 left-1 right-1">
          <div className="flex space-x-1">
            {Array.from({ length: Math.min(day.totalTasks, 5) }).map((_, index) => (
              <div
                key={index}
                className={`h-1 flex-1 rounded ${
                  index < day.completedTasks ? 'bg-green-500' : 'bg-gray-300'
                }`}
              />
            ))}
            {day.totalTasks > 5 && (
              <span className="text-xs text-gray-500">+</span>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default MonthScreen;
