import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { authAPI, taskAPI } from '../services/api';

const AppContext = createContext();

// ÂàùÂßãÁä∂ÊÄÅ
const initialDate = new Date();
const savedSystemDate = localStorage.getItem('systemDate');
const currentSystemDate = savedSystemDate ? new Date(savedSystemDate) : new Date(initialDate);

const initialState = {
  user: null,
  isAuthenticated: false,
  loading: true,
  error: null,
  systemDate: currentSystemDate,
  initialDate: new Date(initialDate)
};

// ActionÁ±ªÂûã
const actionTypes = {
  SET_LOADING: 'SET_LOADING',
  SET_USER: 'SET_USER',
  SET_ERROR: 'SET_ERROR',
  LOGOUT: 'LOGOUT',
  SET_SYSTEM_DATE: 'SET_SYSTEM_DATE'
};

// Reducer
function appReducer(state, action) {
  switch (action.type) {
    case actionTypes.SET_LOADING:
      return { ...state, loading: action.payload };

    case actionTypes.SET_USER:
      return { 
        ...state, 
        user: action.payload, 
        isAuthenticated: !!action.payload,
        loading: false,
        error: null
      };

    case actionTypes.SET_ERROR:
      return { ...state, error: action.payload, loading: false };

    case actionTypes.LOGOUT:
      return { 
        ...state, 
        user: null, 
        isAuthenticated: false, 
        loading: false 
      };

    case actionTypes.SET_SYSTEM_DATE:
      return { ...state, systemDate: action.payload };

    default:
      return state;
  }
}

// Context Provider
export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(appReducer, initialState);

  // ÂàùÂßãÂåñÊó∂Ê£ÄÊü•token
  useEffect(() => {
    const initAuth = async () => {
      const token = localStorage.getItem('token');
      const savedUser = localStorage.getItem('user');

      if (token && savedUser) {
        try {
          // È™åËØÅtokenÊòØÂê¶ÊúâÊïà
          const response = await authAPI.verify();
          if (response.success) {
            dispatch({ 
              type: actionTypes.SET_USER, 
              payload: JSON.parse(savedUser) 
            });
          } else {
            // tokenÊó†ÊïàÔºåÊ∏ÖÈô§Êú¨Âú∞Â≠òÂÇ®
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            dispatch({ type: actionTypes.LOGOUT });
          }
        } catch (error) {
          console.error('TokenÈ™åËØÅÂ§±Ë¥•:', error);
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          dispatch({ type: actionTypes.LOGOUT });
        }
      } else {
        dispatch({ type: actionTypes.SET_LOADING, payload: false });
      }
    };

    initAuth();
  }, []);

  // ÁôªÂΩï
  const login = async (studentId, password, rememberMe = false) => {
    try {
      dispatch({ type: actionTypes.SET_LOADING, payload: true });
      dispatch({ type: actionTypes.SET_ERROR, payload: null });

      const response = await authAPI.login(studentId, password);

      if (response.success) {
        const { token, student } = response.data;

        // ‰øùÂ≠òÂà∞localStorage
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(student));

        if (rememberMe) {
          localStorage.setItem('savedCredentials', JSON.stringify({ 
            studentId, 
            password 
          }));
        } else {
          localStorage.removeItem('savedCredentials');
        }

        dispatch({ type: actionTypes.SET_USER, payload: student });
        return { success: true };
      }
    } catch (error) {
      dispatch({ type: actionTypes.SET_ERROR, payload: error.message });
      return { success: false, message: error.message };
    }
  };

  // ÁôªÂá∫
  const logout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    dispatch({ type: actionTypes.LOGOUT });
  };

  // Âº∫Âà∂‰øÆÊîπÂØÜÁ†Å
  const forceChangePassword = async (newPassword) => {
    try {
      const response = await authAPI.forceChangePassword(newPassword);
      if (response.success) {
        // Êõ¥Êñ∞Áî®Êà∑Áä∂ÊÄÅ
        const updatedUser = { ...state.user, forcePasswordChange: false };
        localStorage.setItem('user', JSON.stringify(updatedUser));
        dispatch({ type: actionTypes.SET_USER, payload: updatedUser });
        return { success: true };
      }
    } catch (error) {
      return { success: false, message: error.message };
    }
  };

  // ‰øÆÊîπÂØÜÁ†Å
  const changePassword = async (oldPassword, newPassword) => {
    try {
      const response = await authAPI.changePassword(oldPassword, newPassword);
      return response;
    } catch (error) {
      return { success: false, message: error.message };
    }
  };

  // ËÆæÁΩÆÁ≥ªÁªüÊó•ÊúüÔºàÁî®‰∫éÊ®°ÊãüÔºâ
  const setSystemDate = (date) => {
    localStorage.setItem('systemDate', date.toISOString());
    dispatch({ type: actionTypes.SET_SYSTEM_DATE, payload: date });
  };

  // ËøõÂÖ•‰∏ã‰∏ÄÂ§©ÔºàÊ®°ÊãüÂäüËÉΩÔºâ
  const advanceDay = async () => {
    try {
      const currentDate = new Date(state.systemDate);
      const nextDay = new Date(currentDate);
      nextDay.setDate(nextDay.getDate() + 1);

      console.log(`üìÖ ‰ªé ${currentDate.toLocaleDateString('zh-CN')} ÂâçËøõÂà∞ ${nextDay.toLocaleDateString('zh-CN')}`);

      // Âú®ÂàáÊç¢Âà∞‰∏ã‰∏ÄÂ§©‰πãÂâçÔºåÂÖàÂ§ÑÁêÜÂΩìÂ§©ÁöÑÊú™ÂÆåÊàê‰ªªÂä°
      await processMidnightTasks(currentDate);

      // ËÆæÁΩÆÊñ∞Êó•Êúü
      setSystemDate(nextDay);

      console.log(`‚úÖ Êó•ÊúüÂàáÊç¢ÂÆåÊàê: ${nextDay.toLocaleDateString('zh-CN')}`);
    } catch (error) {
      console.error('‚ùå advanceDay Â§±Ë¥•:', error);
      throw error; // ÈáçÊñ∞ÊäõÂá∫ÈîôËØØÔºåËÆ©Ë∞ÉÁî®ËÄÖÁü•ÈÅìÂ§±Ë¥•‰∫Ü
    }
  };

  // ÈáçÁΩÆÂà∞ÂàùÂßãÊó•ÊúüÂíåÂàùÂßã‰ªªÂä°Áä∂ÊÄÅ
  const resetToInitialDate = async () => {
    try {
      console.log('üîÑ ÂºÄÂßãÈáçÁΩÆÂà∞ÂàùÂßãÁä∂ÊÄÅ...');

      // Â¶ÇÊûúÁî®Êà∑Â∑≤ÁôªÂΩïÔºåÂÖàÈáçÁΩÆ‰ªªÂä°Êï∞ÊçÆ
      if (state.user) {
        console.log('üóëÔ∏è ÈáçÁΩÆ‰ªªÂä°Êï∞ÊçÆÂà∞ÂàùÂßãÁä∂ÊÄÅ...');
        const response = await taskAPI.resetToInitial();
        if (response.success) {
          console.log('‚úÖ ‰ªªÂä°Êï∞ÊçÆÈáçÁΩÆÊàêÂäü:', response.data);
        } else {
          console.warn('‚ö†Ô∏è ‰ªªÂä°Êï∞ÊçÆÈáçÁΩÆÂ§±Ë¥•:', response.message);
        }
      }

      // ÈáçÁΩÆÊó•Êúü
      console.log('üìÖ ÈáçÁΩÆÊó•ÊúüÂà∞ÂàùÂßãÁä∂ÊÄÅ...');
      localStorage.removeItem('systemDate');
      setSystemDate(new Date(state.initialDate));

      console.log('‚úÖ ÈáçÁΩÆÂÆåÊàêÔºÅ');
    } catch (error) {
      console.error('‚ùå ÈáçÁΩÆÂ§±Ë¥•:', error);
      // Âç≥‰ΩøAPIË∞ÉÁî®Â§±Ë¥•Ôºå‰ªçÁÑ∂ÈáçÁΩÆÊó•Êúü
      localStorage.removeItem('systemDate');
      setSystemDate(new Date(state.initialDate));
    }
  };

  // Ê£ÄÊü•ÊòØÂê¶ÂèØ‰ª•ËÆæÁΩÆÊó•ÊúüÔºàÂè™ËÉΩËÆæÁΩÆ‰ªäÂ§©ÊàñÊú™Êù•ÁöÑÊó•ÊúüÔºâ
  const canSetDate = (targetDate) => {
    const today = new Date(state.initialDate);
    today.setHours(0, 0, 0, 0);
    const target = new Date(targetDate);
    target.setHours(0, 0, 0, 0);
    return target >= today;
  };

  // ÂÆâÂÖ®ËÆæÁΩÆÁ≥ªÁªüÊó•ÊúüÔºàÂè™ÂÖÅËÆ∏ËÆæÁΩÆ‰ªäÂ§©ÊàñÊú™Êù•Êó•ÊúüÔºâ
  const setSystemDateSafely = async (date) => {
    if (canSetDate(date)) {
      const currentDate = new Date(state.systemDate);
      const targetDate = new Date(date);

      // Â¶ÇÊûúÊòØË∑≥Ë∑ÉÂà∞Êú™Êù•Êó•ÊúüÔºåÈúÄË¶ÅÂ§ÑÁêÜ‰∏≠Èó¥ÊâÄÊúâÊó•ÊúüÁöÑ24:00‰ªªÂä°
      if (targetDate > currentDate) {
        let processDate = new Date(currentDate);
        while (processDate < targetDate) {
          await processMidnightTasks(processDate);
          processDate.setDate(processDate.getDate() + 1);
        }
      }

      setSystemDate(date);
      return true;
    }
    return false;
  };

  // Â§ÑÁêÜ24:00‰ªªÂä°ÔºàËá™Âä®Ë∞ÉÁî®Ôºâ
  const processMidnightTasks = async (targetDate) => {
    try {
      // Âè™ÊúâÂú®Áî®Êà∑Â∑≤ÁôªÂΩïÁöÑÊÉÖÂÜµ‰∏ãÊâçÂ§ÑÁêÜ
      if (!state.user) {
        console.log('Áî®Êà∑Êú™ÁôªÂΩïÔºåË∑≥Ëøá24:00‰ªªÂä°Â§ÑÁêÜ');
        return;
      }

      const dateStr = targetDate.toISOString().split('T')[0];
