import React, { useState, useEffect } from 'react';
import { adminAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';
import { performAdminReset, syncPasswordReset } from '../utils/dataConsistency';
import * as XLSX from 'xlsx';

const AdminDashboard = () => {
  const { user, logout } = useApp();
  const [students, setStudents] = useState([]);
  const [selectedStudent, setSelectedStudent] = useState(null);
  const [studentProfile, setStudentProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newStudentName, setNewStudentName] = useState('');

  const [showExcelImportForm, setShowExcelImportForm] = useState(false);
  const [excelFile, setExcelFile] = useState(null);
  const [excelData, setExcelData] = useState([]);

  const [taskReport, setTaskReport] = useState(null);
  const [reportDate, setReportDate] = useState(new Date().toISOString().split('T')[0]);
  const [testMode, setTestMode] = useState(false);
  const [showTaskForm, setShowTaskForm] = useState(false);
  const [newTask, setNewTask] = useState({
    studentId: '',
    title: '',
    date: new Date().toISOString().split('T')[0],
    type: 'Â≠¶‰π†'
  });

  // Ëé∑ÂèñÂ≠¶ÁîüÂàóË°®
  const fetchStudents = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await adminAPI.getStudents();

      if (response.success) {
        setStudents(response.data || []);
      } else {
        setError(response.message || 'Ëé∑ÂèñÂ≠¶ÁîüÂàóË°®Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÁΩëÁªúÈîôËØØ');
    } finally {
      setLoading(false);
    }
  };

  // ‰øÆÂ§çÊï∞ÊçÆÂ∫ì
  const handleFixDatabase = async () => {
    if (!window.confirm('Á°ÆÂÆöË¶Å‰øÆÂ§çÊï∞ÊçÆÂ∫ìÂêóÔºüËøôÂ∞ÜÂàõÂª∫Áº∫Â§±ÁöÑË°®ÂíåÂ≠óÊÆµ„ÄÇ')) {
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('token');
      const response = await fetch('/api/admin/fix-database', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const result = await response.json();

      if (result.success) {
        alert('Êï∞ÊçÆÂ∫ì‰øÆÂ§çÊàêÂäüÔºÅ');
      } else {
        setError(result.message || 'Êï∞ÊçÆÂ∫ì‰øÆÂ§çÂ§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÁΩëÁªúÈîôËØØ');
    } finally {
      setLoading(false);
    }
  };

  // Ëé∑ÂèñÂ≠¶ÁîüÊ°£Ê°à
  const fetchStudentProfile = async (studentId) => {
    try {
      const response = await adminAPI.getStudentProfile(studentId);

      if (response.success) {
        setStudentProfile(response.data);
      } else {
        setError(response.message || 'Ëé∑ÂèñÂ≠¶ÁîüÊ°£Ê°àÂ§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'Ëé∑ÂèñÂ≠¶ÁîüÊ°£Ê°àÂ§±Ë¥•');
    }
  };

  // ÂàõÂª∫Â≠¶Áîü
  const createStudent = async () => {
    if (!newStudentName.trim()) {
      setError('ËØ∑ËæìÂÖ•Â≠¶ÁîüÂßìÂêç');
      return;
    }

    try {
      setError(null);

      const response = await adminAPI.createStudent(newStudentName);

      if (response.success) {
        setNewStudentName('');
        setShowCreateForm(false);
        await fetchStudents();
        alert('Â≠¶ÁîüÂàõÂª∫ÊàêÂäü');
      } else {
        setError(response.message || 'ÂàõÂª∫Â≠¶ÁîüÂ§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÂàõÂª∫Â≠¶ÁîüÂ§±Ë¥•');
    }
  };

  // ÈáçÁΩÆÂØÜÁ†Å
  const resetPassword = async (studentId) => {
    if (!window.confirm('Á°ÆÂÆöË¶ÅÈáçÁΩÆËØ•Â≠¶ÁîüÁöÑÂØÜÁ†ÅÂêóÔºü')) {
      return;
    }

    try {
      setError(null);

      console.log(`üîÑ ÈáçÁΩÆÂ≠¶Áîü ${studentId} ÁöÑÂØÜÁ†Å...`);

      const response = await adminAPI.resetPassword(studentId);

      if (response.success) {
        console.log('‚úÖ ÂØÜÁ†ÅÈáçÁΩÆÊàêÂäü:', response.data);

        // ‰ΩøÁî®Êï∞ÊçÆ‰∏ÄËá¥ÊÄßÂ∑•ÂÖ∑ÂêåÊ≠•Áä∂ÊÄÅ
        syncPasswordReset(studentId, setStudents, setSelectedStudent);

        alert(`‚úÖ ÂØÜÁ†ÅÈáçÁΩÆÊàêÂäüÔºÅ\nÊñ∞ÂØÜÁ†ÅÔºö${response.data.initialPassword}\nËØ•Â≠¶Áîü‰∏ãÊ¨°ÁôªÂΩïÊó∂ÈúÄË¶Å‰øÆÊîπÂØÜÁ†Å„ÄÇ`);
      } else {
        setError(response.message || 'ÈáçÁΩÆÂØÜÁ†ÅÂ§±Ë¥•');
      }
    } catch (err) {
      console.error('‚ùå ÈáçÁΩÆÂØÜÁ†ÅÂ§±Ë¥•:', err);
      setError(err.message || 'ÈáçÁΩÆÂØÜÁ†ÅÂ§±Ë¥•');
    }
  };

  // ÂàõÂª∫Âçï‰∏™‰ªªÂä°
  const createTask = async () => {
    if (!newTask.studentId || !newTask.title.trim()) {
      setError('ËØ∑ÈÄâÊã©Â≠¶ÁîüÂπ∂ËæìÂÖ•‰ªªÂä°Ê†áÈ¢ò');
      return;
    }

    try {
      setError(null);

      const response = await adminAPI.createTask(newTask);

      if (response.success) {
        setNewTask({
          studentId: '',
          title: '',
          date: new Date().toISOString().split('T')[0],
          type: 'Â≠¶‰π†'
        });
        setShowTaskForm(false);
        alert('‰ªªÂä°ÂàõÂª∫ÊàêÂäü');
      } else {
        setError(response.message || 'ÂàõÂª∫‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÂàõÂª∫‰ªªÂä°Â§±Ë¥•');
    }
  };

  // Â§ÑÁêÜExcelÊñá‰ª∂‰∏ä‰º†
  const handleExcelFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) {
      return;
    }

    // Ê∏ÖÁ©∫inputÁöÑÂÄºÔºåÈÅøÂÖçÈáçÂ§çÈÄâÊã©Âêå‰∏ÄÊñá‰ª∂Êó∂‰∏çËß¶Âèëchange‰∫ã‰ª∂
    event.target.value = '';

    console.log('ÈÄâÊã©ÁöÑÊñá‰ª∂:', file.name, file.type, file.size);

    // È™åËØÅÊñá‰ª∂Á±ªÂûã
    const validTypes = [
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // .xlsx
      'application/vnd.ms-excel' // .xls
    ];

    if (!validTypes.includes(file.type) && !file.name.match(/\.(xlsx|xls)$/i)) {
      setError('ËØ∑ÈÄâÊã©ÊúâÊïàÁöÑExcelÊñá‰ª∂ (.xlsx Êàñ .xls)');
      return;
    }

    // È™åËØÅÊñá‰ª∂Â§ßÂ∞è (ÊúÄÂ§ß5MB)
    if (file.size > 5 * 1024 * 1024) {
      setError('Êñá‰ª∂Â§ßÂ∞è‰∏çËÉΩË∂ÖËøá5MB');
      return;
    }

    setExcelFile(file);
    setError(null);

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        console.log('ÂºÄÂßãËß£ÊûêExcelÊñá‰ª∂...');
        const data = new Uint8Array(e.target.result);
        const workbook = XLSX.read(data, { type: 'array' });

        // ËØªÂèñÁ¨¨‰∏Ä‰∏™Â∑•‰ΩúË°®
        const sheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[sheetName];

        // ËΩ¨Êç¢‰∏∫JSONÊ†ºÂºè
        const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
        console.log('Ëß£ÊûêÁöÑÂéüÂßãÊï∞ÊçÆ:', jsonData);

        // ËøáÊª§Á©∫Ë°åÂπ∂Ê†ºÂºèÂåñÊï∞ÊçÆ
        const formattedData = jsonData
          .filter(row => row.length > 0 && row.some(cell => cell !== undefined && cell !== ''))
          .map(row => ({
            studentId: row[0] || '',
            date: row[1] || '',
            taskType: row[2] || '',
            title: row[3] || ''
          }));

        console.log('Ê†ºÂºèÂåñÂêéÁöÑÊï∞ÊçÆ:', formattedData);
        setExcelData(formattedData);
        setError(null);
      } catch (err) {
        console.error('ExcelËß£ÊûêÈîôËØØ:', err);
        setError('ExcelÊñá‰ª∂Ëß£ÊûêÂ§±Ë¥•: ' + err.message);
        setExcelFile(null);
        setExcelData([]);
      }
    };

    reader.onerror = (err) => {
      console.error('Êñá‰ª∂ËØªÂèñÈîôËØØ:', err);
      setError('Êñá‰ª∂ËØªÂèñÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
      setExcelFile(null);
      setExcelData([]);
    };

    reader.readAsArrayBuffer(file);
  };

  // ‰∏ãËΩΩExcelÊ®°Êùø
  const downloadExcelTemplate = () => {
    try {
      // ÂàõÂª∫Á§∫‰æãÊï∞ÊçÆ - ‰ªé7Êúà19Âè∑Âà∞7Êúà31Âè∑ÁöÑÂÆåÊï¥Á§∫‰æã
      const templateData = [
        ['Â≠¶ÁîüID', 'Êó•Êúü', '‰ªªÂä°Á±ªÂûã', '‰ªªÂä°Ê†áÈ¢ò'],
        // 7Êúà19Êó• - Âë®ÂÖ≠
        ['ST001', '2025-07-19', 'Êï∞Â≠¶', 'È´òÁ≠âÊï∞Â≠¶ÂæÆÂàÜÂ≠¶'],
        ['ST001', '2025-07-19', 'Ëã±ËØ≠', 'ËÄÉÁ†îËØçÊ±áUnit1-10'],
        ['ST001', '2025-07-19', '‰∏ì‰∏öËØæ', 'Êï∞ÊçÆÁªìÊûÑ‰∏éÁÆóÊ≥ïÂü∫Á°Ä'],
        ['ST002', '2025-07-19', 'Êï∞Â≠¶', 'Á∫øÊÄß‰ª£Êï∞Áü©ÈòµËøêÁÆó'],
        ['ST002', '2025-07-19', 'Ëã±ËØ≠', 'ÈòÖËØªÁêÜËß£‰∏ìÈ°πËÆ≠ÁªÉ'],
        ['ST002', '2025-07-19', '‰∏ì‰∏öËØæ', 'ËÆ°ÁÆóÊú∫ÁΩëÁªúTCP/IP'],
        // 7Êúà20Êó• - Âë®Êó•Ôºà‰ºëÊÅØÊó•Ôºâ
        ['ST001', '2025-07-20', '‰ºëÊÅØ', '‰ºëÊÅØÊó•'],
        ['ST002', '2025-07-20', '‰ºëÊÅØ', '‰ºëÊÅØÊó•'],
        // 7Êúà21Êó• - Âë®‰∏Ä
        ['ST001', '2025-07-21', 'Êï∞Â≠¶', 'È´òÁ≠âÊï∞Â≠¶ÁßØÂàÜÂ≠¶'],
        ['ST001', '2025-07-21', 'Ëã±ËØ≠', 'ÈòÖËØªÁêÜËß£‰∏ìÈ°πËÆ≠ÁªÉ'],
        ['ST001', '2025-07-21', '‰∏ì‰∏öËØæ', 'Êìç‰ΩúÁ≥ªÁªüËøõÁ®ãÁÆ°ÁêÜ'],
        ['ST002', '2025-07-21', 'Êï∞Â≠¶', 'Ê¶ÇÁéáËÆ∫‰∏éÊï∞ÁêÜÁªüËÆ°'],
        ['ST002', '2025-07-21', 'Ëã±ËØ≠', 'ÂÆåÂΩ¢Â°´Á©∫‰∏ìÈ°πËÆ≠ÁªÉ'],
        ['ST002', '2025-07-21', '‰∏ì‰∏öËØæ', 'Êï∞ÊçÆÂ∫ìÁ≥ªÁªüÂéüÁêÜ'],
        // 7Êúà22Êó• - Âë®‰∫å
        ['ST001', '2025-07-22', 'Êï∞Â≠¶', 'Á∫øÊÄß‰ª£Êï∞ÂêëÈáèÁ©∫Èó¥'],
        ['ST001', '2025-07-22', 'Ëã±ËØ≠', 'ÁøªËØëÊäÄÂ∑ßËÆ≠ÁªÉ'],
        ['ST001', '2025-07-22', '‰∏ì‰∏öËØæ', 'ËÆ°ÁÆóÊú∫ÁªÑÊàêÂéüÁêÜ'],
        ['ST002', '2025-07-22', 'Êï∞Â≠¶', 'È´òÁ≠âÊï∞Â≠¶ÊûÅÈôêÁêÜËÆ∫'],
        ['ST002', '2025-07-22', 'Ëã±ËØ≠', 'ÂÜô‰ΩúÊ®°ÊùøÁªÉ‰π†'],
        ['ST002', '2025-07-22', '‰∏ì‰∏öËØæ', 'ËΩØ‰ª∂Â∑•Á®ãÂü∫Á°Ä'],
        // 7Êúà23Êó• - Âë®‰∏â
        ['ST001', '2025-07-23', 'Êï∞Â≠¶', 'Ê¶ÇÁéáËÆ∫Âü∫Á°ÄÊ¶ÇÂøµ'],
        ['ST001', '2025-07-23', 'Ëã±ËØ≠', 'ÈïøÈöæÂè•ÂàÜÊûê'],
        ['ST001', '2025-07-23', '‰∏ì‰∏öËØæ', 'ÁÆóÊ≥ïËÆæËÆ°‰∏éÂàÜÊûê'],
        ['ST002', '2025-07-23', 'Êï∞Â≠¶', 'Á∫øÊÄß‰ª£Êï∞Áü©ÈòµÂèòÊç¢'],
        ['ST002', '2025-07-23', 'Ëã±ËØ≠', 'ËØçÊ±áËÆ∞ÂøÜÂº∫Âåñ'],
        ['ST002', '2025-07-23', '‰∏ì‰∏öËØæ', 'ÁºñËØëÂéüÁêÜÂü∫Á°Ä'],
