import React, { useState, useEffect } from 'react';
import { useApp } from '../contexts/AppContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const { login, loading, error, user, isAuthenticated } = useApp();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    studentId: '',
    password: '',
    rememberMe: false
  });
  const [loginError, setLoginError] = useState('');
  const [quickLoginStatus, setQuickLoginStatus] = useState('');
  const [hasRedirected, setHasRedirected] = useState(false);

  // Ë∞ÉËØïÔºöÁõëÊéßÁä∂ÊÄÅÂèòÂåñ
  console.log('üîç Login ÁªÑ‰ª∂Ê∏≤Êüì - ÂΩìÂâçÁä∂ÊÄÅ:', {
    isAuthenticated,
    user: user ? `${user.id || user.studentId}` : null,
    hasRedirected,
    loading,
    timestamp: new Date().toISOString()
  });

  // Âä†ËΩΩ‰øùÂ≠òÁöÑÁôªÂΩï‰ø°ÊÅØ
  useEffect(() => {
    try {
      const savedCredentials = localStorage.getItem('savedCredentials');
      if (savedCredentials) {
        const { studentId, password } = JSON.parse(savedCredentials);
        setFormData(prev => ({
          ...prev,
          studentId,
          password,
          rememberMe: true
        }));
      }
    } catch (error) {
      console.error('Âä†ËΩΩ‰øùÂ≠òÁöÑÁôªÂΩï‰ø°ÊÅØÂ§±Ë¥•:', error);
    }
  }, []);

  // ÁõëÂê¨ËÆ§ËØÅÁä∂ÊÄÅÂèòÂåñÔºåÁôªÂΩïÊàêÂäüÂêéËá™Âä®ÈáçÂÆöÂêë
  useEffect(() => {
    console.log('üîç useEffect Ëß¶Âèë - Áä∂ÊÄÅÊ£ÄÊü•:', {
      isAuthenticated,
      user: user ? `${user.id || user.studentId}` : null,
      hasRedirected,
      userObject: user
    });

    console.log('üîç Êù°‰ª∂Ê£ÄÊü•:', {
      'isAuthenticated': isAuthenticated,
      'userÂ≠òÂú®': !!user,
      'hasRedirected': hasRedirected,
      'Êù°‰ª∂Êª°Ë∂≥': isAuthenticated && user && !hasRedirected
    });

    if (isAuthenticated && user && !hasRedirected) {
      console.log('üîÑ Ê£ÄÊµãÂà∞ÁôªÂΩïÊàêÂäüÔºåÂáÜÂ§áÈáçÂÆöÂêë...');
      console.log('üë§ Áî®Êà∑‰ø°ÊÅØ:', user);



      // Ê£ÄÊü•ÊòØÂê¶ÊòØÁÆ°ÁêÜÂëò
      const isAdmin = user && (
        user.userType === 'admin' ||
        user.role === 'admin' ||
        user.studentId?.startsWith('ADMIN') ||
        user.id?.startsWith('ADMIN') ||
        ['ADMIN001', 'ADMIN002'].includes(user.studentId) ||
        ['ADMIN001', 'ADMIN002'].includes(user.id)
      );

      console.log('üîç Áî®Êà∑ËßíËâ≤Ê£ÄÊü•:', { isAdmin, userType: user.userType, id: user.id });

      // ËÆæÁΩÆÈáçÂÆöÂêëÊ†áÂøóÔºåÈò≤Ê≠¢ÈáçÂ§çÈáçÂÆöÂêë
      setHasRedirected(true);

      // Ê†πÊçÆÁî®Êà∑ËßíËâ≤ÈáçÂÆöÂêë
      if (isAdmin) {
        console.log('‚û°Ô∏è ÈáçÂÆöÂêëÂà∞ÁÆ°ÁêÜÂëòÁ´Ø...');
        navigate('/admin', { replace: true });
      } else {
        console.log('‚û°Ô∏è ÈáçÂÆöÂêëÂà∞Â≠¶ÁîüÁ´Ø...');
        navigate('/student', { replace: true });
      }
    }
  }, [isAuthenticated, user, hasRedirected, navigate]);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoginError('');

    if (!formData.studentId.trim() || !formData.password.trim()) {
      setLoginError('ËØ∑ËæìÂÖ•Â≠¶ÁîüIDÂíåÂØÜÁ†Å');
      return;
    }

    console.log('üöÄ ÂºÄÂßãÁôªÂΩïÊµÅÁ®ã...');
    const result = await login(
      formData.studentId.toUpperCase().trim(),
      formData.password,
      formData.rememberMe
    );

    console.log('üìã ÁôªÂΩïÁªìÊûú:', result);
    if (!result.success) {
      setLoginError(result.message);
    } else {
      console.log('‚úÖ ÁôªÂΩïÊàêÂäüÔºåÁ≠âÂæÖÁä∂ÊÄÅÊõ¥Êñ∞ÂíåÈáçÂÆöÂêë...');
    }
  };

  // Âø´ÈÄüÁôªÂΩïÂäüËÉΩ
  const handleQuickLogin = async (studentId, password) => {
    console.log(`üöÄ Âø´ÈÄüÁôªÂΩïÂºÄÂßã: ${studentId}`);

    setLoginError('');
    setQuickLoginStatus(`Ê≠£Âú®ÁôªÂΩï ${studentId}...`);

    try {
