import React, { useState, useEffect } from 'react';
import { useApp } from '../contexts/AppContext';

const Login = ({ adminMode = false }) => {
  const { login, loading, error } = useApp();
  const [formData, setFormData] = useState({
    studentId: '',
    password: '',
    rememberMe: false
  });
  const [loginError, setLoginError] = useState('');

  // Âä†ËΩΩ‰øùÂ≠òÁöÑÁôªÂΩï‰ø°ÊÅØ
  useEffect(() => {
    try {
      const savedCredentials = localStorage.getItem('savedCredentials');
      if (savedCredentials) {
        const { studentId, password } = JSON.parse(savedCredentials);
        setFormData(prev => ({
          ...prev,
          studentId,
          password,
          rememberMe: true
        }));
      }
    } catch (error) {
      console.error('Âä†ËΩΩ‰øùÂ≠òÁöÑÁôªÂΩï‰ø°ÊÅØÂ§±Ë¥•:', error);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoginError('');

    if (!formData.studentId.trim() || !formData.password.trim()) {
      setLoginError('ËØ∑ËæìÂÖ•Â≠¶ÁîüIDÂíåÂØÜÁ†Å');
      return;
    }

    const result = await login(
      formData.studentId.toUpperCase().trim(), 
      formData.password, 
      formData.rememberMe
    );

    if (!result.success) {
      setLoginError(result.message);
    }
  };

  // Âø´ÈÄüÁôªÂΩïÂäüËÉΩ
  const handleQuickLogin = async (studentId, password) => {
    console.log(`üöÄ Âø´ÈÄüÁôªÂΩïÂºÄÂßã: ${studentId}`);
    setLoginError('');

    try {
      const result = await login(studentId, password, false);
      console.log(`üìä ÁôªÂΩïÁªìÊûú:`, result);

      if (!result.success) {
        console.error(`‚ùå ÁôªÂΩïÂ§±Ë¥•: ${result.message}`);
        setLoginError(result.message);
      } else {
        console.log(`‚úÖ ÁôªÂΩïÊàêÂäü: ${studentId}`);
      }
    } catch (error) {
      console.error(`‚ùå ÁôªÂΩïÂºÇÂ∏∏:`, error);
      setLoginError(error.message || 'ÁôªÂΩïËøáÁ®ã‰∏≠ÂèëÁîüÈîôËØØ');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-full p-8 bg-gray-100">
      <div className="w-full max-w-md">
        <div className="text-center mb-8">
          <img 
            src="https://i.postimg.cc/pdmk3gkb/e9398b353525a8d77a431307d7e0cd1a.png" 
            alt="Logo" 
            className="w-16 h-16 mx-auto mb-4 object-contain"
          />
          <h1 className="text-3xl font-bold text-gray-800">{adminMode ? 'ÁÆ°ÁêÜÂëòÁôªÂΩï' : 'Â≠¶ÁîüÁôªÂΩï'}</h1>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <input
              type="text"
              name="studentId"
              placeholder={adminMode ? "ÁÆ°ÁêÜÂëòID (‰æãÂ¶Ç: ADMIN001)" : "Â≠¶ÁîüID (‰æãÂ¶Ç: ST001)"}
              value={formData.studentId}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              disabled={loading}
            />
          </div>

          <div>
            <input
              type="password"
              name="password"
              placeholder="ÂØÜÁ†Å"
              value={formData.password}
              onChange={handleChange}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              disabled={loading}
            />
          </div>

          <div className="flex items-center">
            <input
              id="remember-me"
              name="rememberMe"
              type="checkbox"
              checked={formData.rememberMe}
              onChange={handleChange}
              className="h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
              disabled={loading}
            />
            <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-900">
              ËÆ∞‰ΩèÂØÜÁ†Å
            </label>
          </div>

          {(loginError || error) && (
            <div className="p-3 bg-red-100 border border-red-400 text-red-700 rounded-md text-sm">
              {loginError || error}
            </div>
          )}

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white p-3 rounded-md font-semibold hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'ÁôªÂΩï‰∏≠...' : 'ÁôªÂΩï'}
          </button>
        </form>

        <div className="mt-6 text-center text-sm text-gray-500">
          <p>Â≠¶ÁîüÂàùÂßãÂØÜÁ†Å: TestPass123</p>
          <p>ÁÆ°ÁêÜÂëòÂàùÂßãÂØÜÁ†Å: AdminPass123</p>
        </div>

        {/* Âø´ÈÄüÁôªÂΩïÊåâÈíÆ - ‰ªÖÁî®‰∫éÊµãËØï */}
        <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-md">
          <p className="text-sm text-yellow-800 mb-3 font-medium">üß™ ÊµãËØïÂø´ÈÄüÁôªÂΩï</p>
          <div className="space-y-2">
            {adminMode ? (
              <>
                <button
                  onClick={() => handleQuickLogin('ADMIN001', 'AdminPass123')}
                  disabled={loading}
                  className="w-full bg-green-500 text-white p-2 rounded text-sm hover:bg-green-600 disabled:opacity-50"
                >
                  ÁÆ°ÁêÜÂëòADMIN001 (ÂàùÂßãÂØÜÁ†Å)
                </button>
                <button
                  onClick={() => handleQuickLogin('ADMIN002', 'AdminPass123')}
                  disabled={loading}
                  className="w-full bg-green-500 text-white p-2 rounded text-sm hover:bg-green-600 disabled:opacity-50"
                >
                  ÁÆ°ÁêÜÂëòADMIN002 (ÂàùÂßãÂØÜÁ†Å)
                </button>
              </>
            ) : (
              <>
                <button
                  onClick={() => handleQuickLogin('ST001', 'TestPass123')}
                  disabled={loading}
                  className="w-full bg-green-500 text-white p-2 rounded text-sm hover:bg-green-600 disabled:opacity-50"
                >
                  Â≠¶ÁîüST001 (ÂàùÂßãÂØÜÁ†Å)
                </button>
                <button
                  onClick={() => handleQuickLogin('ST002', 'TestPass123')}
                  disabled={loading}
                  className="w-full bg-green-500 text-white p-2 rounded text-sm hover:bg-green-600 disabled:opacity-50"
                >
                  Â≠¶ÁîüST002 (ÂàùÂßãÂØÜÁ†Å)
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
