import React, { useState, useEffect } from 'react';
import { taskAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';

const FutureDaysView = ({ onClose }) => {
  const { systemDate } = useApp();
  const [futureTasks, setFutureTasks] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentDayIndex, setCurrentDayIndex] = useState(0);

  // ÁîüÊàêÊú™Êù•5Â§©ÁöÑÊó•Êúü
  const generateFutureDates = () => {
    const dates = [];
    for (let i = 1; i <= 5; i++) {
      const date = new Date(systemDate);
      date.setDate(date.getDate() + i);
      dates.push({
        date: date,
        dateStr: date.toISOString().split('T')[0],
        displayName: `${date.getMonth() + 1}Êúà${date.getDate()}Êó•`,
        dayName: ['Âë®Êó•', 'Âë®‰∏Ä', 'Âë®‰∫å', 'Âë®‰∏â', 'Âë®Âõõ', 'Âë®‰∫î', 'Âë®ÂÖ≠'][date.getDay()]
      });
    }
    return dates;
  };

  const futureDates = generateFutureDates();

  // Ëé∑ÂèñÊú™Êù•Âá†Â§©ÁöÑ‰ªªÂä°
  const fetchFutureTasks = async () => {
    try {
      setLoading(true);
      setError(null);

      const startDate = futureDates[0].dateStr;
      const endDate = futureDates[futureDates.length - 1].dateStr;

      const response = await taskAPI.getTasks(startDate, endDate);

      if (response.success) {
        setFutureTasks(response.data || {});
      } else {
        setError(response.message || 'Ëé∑Âèñ‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÁΩëÁªúÈîôËØØ');
    } finally {
      setLoading(false);
    }
  };

  // Êõ¥Êñ∞‰ªªÂä°Áä∂ÊÄÅÔºàÊèêÂâçÂÆåÊàêÔºâ
  const updateTaskStatus = async (taskId, completed) => {
    try {
      const response = await taskAPI.updateTask(taskId, { 
        completed,
        completedAt: completed ? new Date().toISOString() : null,
        completed_date: completed ? systemDate.toISOString().split('T')[0] : null,
        is_future_task: completed // Ê†áËÆ∞‰∏∫ÊèêÂâçÂÆåÊàêÁöÑÊú™Êù•‰ªªÂä°
      });

      if (response.success) {
        // ÈáçÊñ∞Ëé∑Âèñ‰ªªÂä°Êï∞ÊçÆ
        await fetchFutureTasks();
      } else {
        setError(response.message || 'Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•');
    }
  };

  useEffect(() => {
    fetchFutureTasks();
  }, [systemDate]);

  // ÊªëÂä®Â§ÑÁêÜ
  const handleSwipeLeft = () => {
    if (currentDayIndex < futureDates.length - 1) {
      setCurrentDayIndex(currentDayIndex + 1);
    }
  };

  const handleSwipeRight = () => {
    if (currentDayIndex > 0) {
      setCurrentDayIndex(currentDayIndex - 1);
    }
  };

  const currentDay = futureDates[currentDayIndex];
  const currentTasks = futureTasks[currentDay?.dateStr] || [];

  if (loading) {
    return (
      <div className="fixed inset-0 bg-white z-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-2 text-gray-600">Âä†ËΩΩ‰∏≠...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-white z-50">
      {/* Â§¥ÈÉ® */}
      <div className="bg-blue-500 text-white p-4">
        <div className="flex items-center justify-between">
          <button onClick={onClose} className="text-white">
            ‚Üê ËøîÂõû
          </button>
          <h1 className="text-lg font-semibold">Êú™Êù•‰ªªÂä°</h1>
          <div className="w-6"></div>
        </div>
      </div>

      {/* Êó•ÊúüÂØºËà™ */}
      <div className="bg-gray-50 px-4 py-3">
        <div className="flex items-center justify-between">
          <button 
            onClick={handleSwipeRight}
            disabled={currentDayIndex === 0}
            className={`p-2 rounded ${currentDayIndex === 0 ? 'text-gray-400' : 'text-blue-600'}`}
          >
            ‚óÄ
          </button>

          <div className="text-center">
            <div className="text-lg font-semibold text-gray-800">
              {currentDay?.displayName}
            </div>
            <div className="text-sm text-gray-600">
              {currentDay?.dayName} ‚Ä¢ ËøòÊúâ{currentDayIndex + 1}Â§©
            </div>
          </div>

          <button 
            onClick={handleSwipeLeft}
            disabled={currentDayIndex === futureDates.length - 1}
            className={`p-2 rounded ${currentDayIndex === futureDates.length - 1 ? 'text-gray-400' : 'text-blue-600'}`}
          >
            ‚ñ∂
          </button>
        </div>

        {/* Êó•ÊúüÊåáÁ§∫Âô® */}
        <div className="flex justify-center mt-3 space-x-2">
          {futureDates.map((_, index) => (
            <div
              key={index}
              className={`w-2 h-2 rounded-full ${
                index === currentDayIndex ? 'bg-blue-500' : 'bg-gray-300'
              }`}
            />
          ))}
        </div>
      </div>

      {error && (
        <div className="mx-4 mt-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded-md">
          {error}
        </div>
      )}

      {/* ‰ªªÂä°ÂàóË°® */}
      <div className="flex-1 overflow-y-auto p-4">
        {currentTasks.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-gray-400 text-6xl mb-4">üóìÔ∏è</div>
            <h3 className="text-lg font-semibold text-gray-600 mb-2">ÊöÇÊó†‰ªªÂä°</h3>
            <p className="text-gray-500">Ëøô‰∏ÄÂ§©Ê≤°ÊúâÂÆâÊéí‰ªªÂä°</p>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="mb-4 p-3 bg-blue-50 rounded-lg">
              <p className="text-blue-800 text-sm">
                üí° <strong>ÊèêÂâçÂÆåÊàêËØ¥ÊòéÔºö</strong> ÊÇ®ÂèØ‰ª•ÊèêÂâçÂÆåÊàêÊú™Êù•5Â§©ÂÜÖÁöÑ‰ªªÂä°„ÄÇ
                ÂÆåÊàêÁöÑ‰ªªÂä°Â∞ÜËÆ∞ÂΩï‰∏∫‰ªäÂ§©ÂÆåÊàêÔºå‰ΩÜ‰∏ç‰ºöÂΩ±ÂìçÂéüÂÆöÁöÑ‰ªªÂä°ÂÆâÊéí„ÄÇ
              </p>
            </div>

            {currentTasks.map((task) => (
              <FutureTaskCard
                key={task.id}
                task={task}
                onStatusChange={updateTaskStatus}
                currentDate={systemDate}
                targetDate={currentDay.date}
              />
            ))}
          </div>
        )}
      </div>

      {/* Â∫ïÈÉ®ÊèêÁ§∫ */}
      <div className="bg-gray-50 p-4 border-t">
        <p className="text-center text-sm text-gray-600">
          Â∑¶Âè≥ÊªëÂä®Êü•ÁúãÂÖ∂‰ªñÊó•ÊúüÁöÑ‰ªªÂä° ‚Ä¢ ÊúÄÂ§öÂèØÊèêÂâçÂÆåÊàê5Â§©ÂÜÖÁöÑ‰ªªÂä°
        </p>
      </div>
    </div>
  );
};

// Êú™Êù•‰ªªÂä°Âç°ÁâáÁªÑ‰ª∂
const FutureTaskCard = ({ task, onStatusChange, currentDate, targetDate }) => {
  const [isCompleting, setIsCompleting] = useState(false);

  const handleStatusToggle = async () => {
    if (isCompleting) return;

    if (!task.completed) {
      // ÊèêÂâçÂÆåÊàêÁ°ÆËÆ§
      const confirmed = window.confirm(
        `Á°ÆÂÆöË¶ÅÊèêÂâçÂÆåÊàêËøô‰∏™‰ªªÂä°ÂêóÔºü\n\n‰ªªÂä°Ôºö"${task.title}"\nËÆ°ÂàíÊó•ÊúüÔºö${targetDate.toLocaleDateString('zh-CN')}\nÂÆåÊàêÊó•ÊúüÔºö${currentDate.toLocaleDateString('zh-CN')}\n\nÊ≠§Êìç‰Ωú‰∏çÂèØÊí§ÈîÄ„ÄÇ`
      );

      if (!confirmed) return;
    }

    setIsCompleting(true);
    try {
      await onStatusChange(task.id, !task.completed);
    } finally {
      setIsCompleting(false);
    }
  };

  return (
    <div className={`border rounded-lg p-4 ${
      task.completed 
        ? 'bg-green-50 border-green-200' 
        : 'bg-white border-gray-200'
    }`}>
      <div className="flex items-start space-x-3">
        <button
          onClick={handleStatusToggle}
          disabled={isCompleting}
          className={`mt-1 w-5 h-5 rounded border-2 flex items-center justify-center ${
            task.completed
              ? 'bg-green-500 border-green-500 text-white'
              : isCompleting
                ? 'border-gray-300 cursor-wait'
                : 'border-gray-300 hover:border-blue-500'
          }`}
        >
          {isCompleting ? (
            <div className="animate-spin w-3 h-3 border border-gray-400 border-t-transparent rounded-full"></div>
          ) : (
            task.completed && (
              <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
              </svg>
            )
          )}
        </button>

        <div className="flex-1">
          <h3 className={`font-semibold ${
            task.completed ? 'text-green-700 line-through' : 'text-gray-800'
          }`}>
            {task.title || '‰ªªÂä°Ê†áÈ¢ò'}
          </h3>

          <div className="mt-2 flex items-center space-x-4 text-sm">
            <span className={`px-2 py-1 rounded text-xs ${
              task.type === 'Êï∞Â≠¶' 
                ? 'bg-blue-100 text-blue-800'
                : task.type === 'Ëã±ËØ≠'
                  ? 'bg-green-100 text-green-800'
                  : task.type === '‰∏ì‰∏öËØæ'
                    ? 'bg-purple-100 text-purple-800'
                    : task.type === '‰ºëÊÅØ'
                      ? 'bg-yellow-100 text-yellow-800'
                      : 'bg-gray-100 text-gray-800'
            }`}>
              {task.type || 'ÂÖ∂‰ªñ'}
            </span>

            {task.completed && (
              <span className="text-green-600 text-xs">
                ‚ú® Â∑≤ÊèêÂâçÂÆåÊàê
              </span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FutureDaysView;
