import React, { useState, useEffect } from 'react';
import { taskAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';

const FutureDaysView = ({ onClose }) => {
  const { systemDate } = useApp();
  const [futureTasks, setFutureTasks] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentDayIndex, setCurrentDayIndex] = useState(0);

  // ÁîüÊàêÊú™Êù•5Â§©ÁöÑÊó•Êúü
  const generateFutureDates = () => {
    const dates = [];
    for (let i = 1; i <= 5; i++) {
      const date = new Date(systemDate);
      date.setDate(date.getDate() + i);
      dates.push({
        date: date,
        dateStr: (() => {
          const year = date.getFullYear();
          const month = String(date.getMonth() + 1).padStart(2, '0');
          const day = String(date.getDate()).padStart(2, '0');
          return `${year}-${month}-${day}`;
        })(),
        displayName: `${date.getMonth() + 1}Êúà${date.getDate()}Êó•`,
        dayName: ['Âë®Êó•', 'Âë®‰∏Ä', 'Âë®‰∫å', 'Âë®‰∏â', 'Âë®Âõõ', 'Âë®‰∫î', 'Âë®ÂÖ≠'][date.getDay()]
      });
    }
    return dates;
  };

  const futureDates = generateFutureDates();

  // Ëé∑ÂèñÊú™Êù•Âá†Â§©ÁöÑ‰ªªÂä°
  const fetchFutureTasks = async () => {
    try {
      setLoading(true);
      setError(null);

      const startDate = futureDates[0].dateStr;
      const endDate = futureDates[futureDates.length - 1].dateStr;

      const response = await taskAPI.getTasks(startDate, endDate);

      if (response.success) {
        setFutureTasks(response.data || {});
      } else {
        setError(response.message || 'Ëé∑Âèñ‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÁΩëÁªúÈîôËØØ');
    } finally {
      setLoading(false);
    }
  };

  // Êõ¥Êñ∞‰ªªÂä°Áä∂ÊÄÅÔºàÊèêÂâçÂÆåÊàêÔºâ
  const updateTaskStatus = async (taskId, completed) => {
    try {
      const response = await taskAPI.updateTask(taskId, { 
        completed,
        completedAt: completed ? new Date().toISOString() : null,
        completed_date: completed ? systemDate.toISOString().split('T')[0] : null,
        is_future_task: completed // Ê†áËÆ∞‰∏∫ÊèêÂâçÂÆåÊàêÁöÑÊú™Êù•‰ªªÂä°
      });

      if (response.success) {
        // ÈáçÊñ∞Ëé∑Âèñ‰ªªÂä°Êï∞ÊçÆ
        await fetchFutureTasks();
      } else {
        setError(response.message || 'Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•');
    }
  };

  useEffect(() => {
    fetchFutureTasks();
  }, [systemDate]);

  // ÊªëÂä®Â§ÑÁêÜ
  const handleSwipeLeft = () => {
    if (currentDayIndex < futureDates.length - 1) {
      setCurrentDayIndex(currentDayIndex + 1);
    }
  };

  const handleSwipeRight = () => {
    if (currentDayIndex > 0) {
      setCurrentDayIndex(currentDayIndex - 1);
    }
  };

  const currentDay = futureDates[currentDayIndex];
  const currentTasks = futureTasks[currentDay?.dateStr] || [];

  if (loading) {
    return (
      <div className="fixed inset-0 bg-white z-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-2 text-gray-600">Âä†ËΩΩ‰∏≠...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-white z-50">
      {/* Â§¥ÈÉ® */}
      <div className="bg-blue-500 text-white p-4">
        <div className="flex items-center justify-between">
          <button onClick={onClose} className="text-white">
            ‚Üê ËøîÂõû
          </button>
          <h1 className="text-lg font-semibold">Êú™Êù•‰ªªÂä°</h1>
          <div className="w-6"></div>
        </div>
      </div>

      {/* Êó•ÊúüÂØºËà™ */}
      <div className="bg-gray-50 px-4 py-3">
        <div className="flex items-center justify-between">
