// Êú™Êù•‰ªªÂä°Âç°ÁâáÁªÑ‰ª∂
const FutureTaskCard = ({ task, onStatusChange, currentDate, targetDate }) => {
  const [isCompleting, setIsCompleting] = useState(false);

  const handleStatusToggle = async () => {
    if (isCompleting) return;

    if (!task.completed) {
      // ÊèêÂâçÂÆåÊàêÁ°ÆËÆ§
      const confirmed = window.confirm(
        `Á°ÆÂÆöË¶ÅÊèêÂâçÂÆåÊàêËøô‰∏™‰ªªÂä°ÂêóÔºü\n\n‰ªªÂä°Ôºö"${task.title}"\nËÆ°ÂàíÊó•ÊúüÔºö${targetDate.toLocaleDateString('zh-CN')}\nÂÆåÊàêÊó•ÊúüÔºö${currentDate.toLocaleDateString('zh-CN')}\n\nÊ≠§Êìç‰Ωú‰∏çÂèØÊí§ÈîÄ„ÄÇ`
      );

      if (!confirmed) return;
    }

    setIsCompleting(true);
    try {
      await onStatusChange(task.id, !task.completed);
    } finally {
      setIsCompleting(false);
    }
  };

  return (
    <div className={`border rounded-lg p-4 ${
      task.completed 
        ? 'bg-green-50 border-green-200' 
        : 'bg-white border-gray-200'
    }`}>
      <div className="flex items-start space-x-3">
        <button
          onClick={handleStatusToggle}
          disabled={isCompleting}
          className={`mt-1 w-5 h-5 rounded border-2 flex items-center justify-center ${
            task.completed
              ? 'bg-green-500 border-green-500 text-white'
              : isCompleting
                ? 'border-gray-300 cursor-wait'
                : 'border-gray-300 hover:border-blue-500'
          }`}
        >
          {isCompleting ? (
            <div className="animate-spin w-3 h-3 border border-gray-400 border-t-transparent rounded-full"></div>
          ) : (
            task.completed && (
              <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
              </svg>
            )
          )}
        </button>

        <div className="flex-1">
          <h3 className={`font-semibold ${
            task.completed ? 'text-green-700 line-through' : 'text-gray-800'
          }`}>
            {task.title || '‰ªªÂä°Ê†áÈ¢ò'}
          </h3>

          <div className="mt-2 flex items-center space-x-4 text-sm">
            <span className={`px-2 py-1 rounded text-xs ${
              task.type === 'Êï∞Â≠¶' 
                ? 'bg-blue-100 text-blue-800'
                : task.type === 'Ëã±ËØ≠'
                  ? 'bg-green-100 text-green-800'
                  : task.type === '‰∏ì‰∏öËØæ'
                    ? 'bg-purple-100 text-purple-800'
                    : task.type === '‰ºëÊÅØ'
                      ? 'bg-yellow-100 text-yellow-800'
                      : 'bg-gray-100 text-gray-800'
            }`}>
              {task.type || 'ÂÖ∂‰ªñ'}
            </span>

            {task.completed && (
              <span className="text-green-600 text-xs">
                ‚ú® Â∑≤ÊèêÂâçÂÆåÊàê
              </span>
            )}
          </div>
        </div>
      </div>
    </div>
  );
