import React, { useState, useEffect } from 'react';
import { taskAPI } from '../services/api';
import { useApp } from '../contexts/AppContext';
import FutureDaysView from './FutureDaysView';

const TodayScreen = () => {
  const { systemDate } = useApp();
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [leaveRecords, setLeaveRecords] = useState([]);
  const [showLeaveConfirm, setShowLeaveConfirm] = useState(false);
  const [showFutureDays, setShowFutureDays] = useState(false);

  // Ê†ºÂºèÂåñÊó•Êúü‰∏∫YYYY-MM-DD
  const formatDate = (date) => {
    return date.toISOString().split('T')[0];
  };

  // Ëé∑Âèñ‰ªäÊó•‰ªªÂä°
  const fetchTodayTasks = async () => {
    try {
      setLoading(true);
      setError(null);

      const today = formatDate(systemDate);
      const response = await taskAPI.getTasks(today, today);

      if (response.success) {
        // ÂêéÁ´ØËøîÂõûÁöÑÊòØÊåâÊó•ÊúüÂàÜÁªÑÁöÑÂØπË±°ÔºåÈúÄË¶ÅÊèêÂèñ‰ªäÂ§©ÁöÑ‰ªªÂä°
        const todayTasks = response.data[today] || [];
        setTasks(todayTasks);
      } else {
        setError(response.message || 'Ëé∑Âèñ‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ÁΩëÁªúÈîôËØØ');
    } finally {
      setLoading(false);
    }
  };

  // Ëé∑ÂèñËØ∑ÂÅáËÆ∞ÂΩï
  const fetchLeaveRecords = async () => {
    try {
      const response = await taskAPI.getLeaveRecords();
      if (response.success) {
        setLeaveRecords(response.data || []);
      }
    } catch (err) {
      console.error('Ëé∑ÂèñËØ∑ÂÅáËÆ∞ÂΩïÂ§±Ë¥•:', err);
    }
  };

  // Êõ¥Êñ∞‰ªªÂä°Áä∂ÊÄÅ
  const updateTaskStatus = async (taskId, completed) => {
    try {
      const response = await taskAPI.updateTask(taskId, { 
        completed,
        completedAt: completed ? new Date().toISOString() : null
      });

      if (response.success) {
        setTasks(prevTasks => 
          prevTasks.map(task => 
            task.id === taskId 
              ? { ...task, completed, completedAt: completed ? new Date().toISOString() : null }
              : task
          )
        );
      } else {
        setError(response.message || 'Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'Êõ¥Êñ∞‰ªªÂä°Â§±Ë¥•');
    }
  };

  // Êõ¥Êñ∞‰ªªÂä°Êó∂Èïø
  const updateTaskDuration = async (taskId, duration) => {
    try {
      const response = await taskAPI.updateTask(taskId, { duration });

      if (response.success) {
        setTasks(prevTasks => 
          prevTasks.map(task => 
            task.id === taskId ? { ...task, duration } : task
          )
        );
      } else {
        setError(response.message || 'Êõ¥Êñ∞Êó∂ÈïøÂ§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'Êõ¥Êñ∞Êó∂ÈïøÂ§±Ë¥•');
    }
  };

  // ÊòæÁ§∫ËØ∑ÂÅáÁ°ÆËÆ§ÂºπÁ™ó
  const showLeaveDialog = () => {
    setShowLeaveConfirm(true);
  };

  // Á°ÆËÆ§Áî≥ËØ∑ËØ∑ÂÅá
  const confirmRequestLeave = async () => {
    try {
      setShowLeaveConfirm(false);
      setLoading(true);

      const today = formatDate(systemDate);
      const response = await taskAPI.requestLeave(today);

      if (response.success) {
        await fetchLeaveRecords();
        await fetchTodayTasks(); // ÈáçÊñ∞Ëé∑Âèñ‰ªªÂä°Êï∞ÊçÆ
        setError(null);
        alert('ËØ∑ÂÅáÁî≥ËØ∑ÊàêÂäüÔºÅ‰ªäÊó•‰ªªÂä°Â∑≤È°∫Âª∂Âà∞ÊòéÊó•ÔºåÊâÄÊúâÂêéÁª≠‰ªªÂä°Ëá™Âä®È°∫Âª∂‰∏ÄÂ§©„ÄÇ');
      } else {
        setError(response.message || 'ËØ∑ÂÅáÁî≥ËØ∑Â§±Ë¥•');
      }
    } catch (err) {
      setError(err.message || 'ËØ∑ÂÅáÁî≥ËØ∑Â§±Ë¥•');
    } finally {
      setLoading(false);
    }
  };

  // ÂèñÊ∂àËØ∑ÂÅá
  const cancelRequestLeave = () => {
    setShowLeaveConfirm(false);
  };

  useEffect(() => {
    fetchTodayTasks();
    fetchLeaveRecords();
  }, [systemDate]);

  // Ê£ÄÊü•‰ªäÊó•ÊòØÂê¶Â∑≤ËØ∑ÂÅá
  const isOnLeaveToday = () => {
    const today = formatDate(systemDate);
    return leaveRecords.some(record => 
      record.date === today && record.status === 'approved'
    );
  };

  // ËÆ°ÁÆó‰ªäÊó•ÂÆåÊàêÁéá
  const getCompletionRate = () => {
    const taskArray = Array.isArray(tasks) ? tasks : [];
    if (taskArray.length === 0) return 0;
    const completedTasks = taskArray.filter(task => task.completed).length;
    return Math.round((completedTasks / taskArray.length) * 100);
  };

  if (loading) {
    return (
      <div data-testid="today-screen" className="p-6">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-2 text-gray-600">Âä†ËΩΩ‰∏≠...</p>
        </div>
      </div>
    );
  }

  // Ê£ÄÊü•ÊòØÂê¶ÊúâËÆ§ËØÅÈîôËØØ
  if (error && error.includes('‰ª§Áâå')) {
    return (
      <div data-testid="today-screen" className="p-6">
        <div className="text-center">
          <div className="text-6xl mb-4">üîí</div>
          <h3 className="text-lg font-semibold text-gray-600 mb-2">ÈúÄË¶ÅÁôªÂΩï</h3>
          <p className="text-gray-500 mb-4">ËØ∑ÂÖàÁôªÂΩïÂêéÊü•Áúã‰ªªÂä°</p>
          <button
            onClick={() => window.location.href = '/login'}
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
          >
            ÂâçÂæÄÁôªÂΩï
          </button>
        </div>
      </div>
    );
  }

  return (
    <div data-testid="today-screen" className="p-6 max-w-4xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-800 mb-2">
          ‰ªäÊó•‰ªªÂä° - {systemDate.toLocaleDateString('zh-CN')}
        </h1>
        <div className="flex items-center justify-between">
          <div className="text-sm text-gray-600">
            ÂÆåÊàêÁéá: <span className="font-semibold text-blue-600">{getCompletionRate()}%</span>
            {Array.isArray(tasks) && tasks.length > 0 && (
              <span className="ml-2">
                ({tasks.filter(t => t.completed).length}/{tasks.length} È°πÂÆåÊàê)
              </span>
            )}
          </div>
          <div className="flex space-x-2">
            <button
              onClick={() => setShowFutureDays(true)}
              className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 text-sm"
            >
              Êú™Êù•‰ªªÂä° ‚Üí
            </button>
            {!isOnLeaveToday() && (
              <button
                onClick={showLeaveDialog}
                className="px-4 py-2 bg-yellow-500 text-white rounded-md hover:bg-yellow-600 text-sm"
              >
                ‰ªäÊó•ËØ∑ÂÅá
              </button>
            )}
          </div>
        </div>
      </div>

      {error && (
        <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded-md">
          {error}
        </div>
      )}

      {isOnLeaveToday() && (
        <div className="mb-6 p-4 bg-yellow-100 border border-yellow-400 text-yellow-800 rounded-md">
          <p className="font-semibold">‰ªäÊó•Â∑≤ËØ∑ÂÅá</p>
          <p className="text-sm">ÊÇ®‰ªäÊó•ÁöÑËØ∑ÂÅáÁî≥ËØ∑Â∑≤ÈÄöËøáÔºåÊó†ÈúÄÂÆåÊàê‰ªªÂä°„ÄÇ</p>
        </div>
      )}

      {(Array.isArray(tasks) ? tasks : []).length === 0 ? (
        <div className="text-center py-8">
          <div className="text-gray-400 text-6xl mb-4">üìù</div>
          <h3 className="text-lg font-semibold text-gray-600 mb-2">ÊöÇÊó†‰ªäÊó•‰ªªÂä°</h3>
          <p className="text-gray-500">‰ªäÂ§©Ê≤°ÊúâÂÆâÊéí‰ªªÂä°ÔºåÊÇ®ÂèØ‰ª•‰ºëÊÅØ‰∏Ä‰∏ã„ÄÇ</p>
        </div>
      ) : (
        <div className="space-y-4">
          {(Array.isArray(tasks) ? tasks : []).map((task) => (
            <TaskCard
              key={task.id}
              task={task}
              onStatusChange={updateTaskStatus}
              onDurationChange={updateTaskDuration}
              disabled={isOnLeaveToday()}
            />
          ))}
        </div>
      )}

      {/* ËØ∑ÂÅáÁ°ÆËÆ§ÂºπÁ™ó */}
      {showLeaveConfirm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white rounded-lg shadow-xl p-6 w-11/12 max-w-md">
            <div className="text-center">
              <div className="text-6xl mb-4">‚ö†Ô∏è</div>
              <h3 className="text-xl font-bold text-gray-800 mb-2">Á°ÆËÆ§ËØ∑ÂÅá</h3>
              <div className="text-gray-600 mb-6 text-left space-y-2">
                <p>‚Ä¢ ‰ªäÊó•ÊâÄÊúâÊú™ÂÆåÊàê‰ªªÂä°Â∞ÜÈ°∫Âª∂Âà∞ÊòéÊó•</p>
                <p>‚Ä¢ ÂêéÁª≠ÊâÄÊúâ‰ªªÂä°Ëá™Âä®È°∫Âª∂‰∏ÄÂ§©</p>
                <p>‚Ä¢ Â¶ÇÊûúÊòéÊó•ÊòØ‰ºëÊÅØÊó•Ôºå‰ªªÂä°Â∞ÜË∑≥Ëøá‰ºëÊÅØÊó•</p>
                <p className="text-red-600 font-semibold">‚Ä¢ Ê≠§Êìç‰Ωú‰∏çÂèØÊí§ÈîÄ</p>
              </div>
              <p className="text-lg font-semibold text-gray-800 mb-6">
                ÊòØÂê¶Á°ÆËÆ§ËØ∑ÂÅáÔºü
              </p>
              <div className="flex space-x-4">
                <button
                  onClick={cancelRequestLeave}
                  className="flex-1 px-4 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600"
                >
                  ÂèñÊ∂à
                </button>
                <button
                  onClick={confirmRequestLeave}
                  className="flex-1 px-4 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
                >
                  Á°ÆËÆ§ËØ∑ÂÅá
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Êú™Êù•‰ªªÂä°ËßÜÂõæ */}
      {showFutureDays && (
        <FutureDaysView onClose={() => setShowFutureDays(false)} />
      )}
    </div>
  );
};

// ‰ªªÂä°Âç°ÁâáÁªÑ‰ª∂
const TaskCard = ({ task, onStatusChange, onDurationChange, disabled }) => {
  const [duration, setDuration] = useState({
    hour: task.duration?.hour || 0,
    minute: task.duration?.minute || 0
  });
  const [isEditing, setIsEditing] = useState(false);
  const [showImageUpload, setShowImageUpload] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(task.proof || null);

  const handleStatusToggle = () => {
    if (disabled) return;
    onStatusChange(task.id, !task.completed);
  };

  const handleDurationSave = () => {
    onDurationChange(task.id, duration);
    setIsEditing(false);
  };

  const formatDuration = () => {
    if (!task.duration || (task.duration.hour === 0 && task.duration.minute === 0)) {
      return 'Êú™ËÆ∞ÂΩï';
    }
    return `${task.duration.hour}Â∞èÊó∂${task.duration.minute}ÂàÜÈíü`;
  };

  // Â§ÑÁêÜÂõæÁâáÈÄâÊã©
  const handleImageSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // Ê£ÄÊü•Êñá‰ª∂Â§ßÂ∞èÔºàÈôêÂà∂‰∏∫5MBÔºâ
      if (file.size > 5 * 1024 * 1024) {
        alert('ÂõæÁâáÂ§ßÂ∞è‰∏çËÉΩË∂ÖËøá5MB');
        return;
      }

      // Ê£ÄÊü•Êñá‰ª∂Á±ªÂûã
      if (!file.type.startsWith('image/')) {
        alert('ËØ∑ÈÄâÊã©ÂõæÁâáÊñá‰ª∂');
        return;
      }

      setSelectedImage(file);

      // ÂàõÂª∫È¢ÑËßà
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  // ‰∏ä‰º†ÂõæÁâá
  const handleImageUpload = async () => {
    if (!selectedImage) return;

    try {
      const reader = new FileReader();
      reader.onload = async (e) => {
        const base64Image = e.target.result;

        // Ë∞ÉÁî®API‰∏ä‰º†ÂõæÁâá
        await updateTaskProof(task.id, base64Image);
        setShowImageUpload(false);
        setSelectedImage(null);
      };
      reader.readAsDataURL(selectedImage);
    } catch (error) {
      console.error('ÂõæÁâá‰∏ä‰º†Â§±Ë¥•:', error);
      alert('ÂõæÁâá‰∏ä‰º†Â§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  // Êõ¥Êñ∞‰ªªÂä°Âá≠ËØÅ
  const updateTaskProof = async (taskId, proofImage) => {
    try {
      const response = await taskAPI.updateTask(taskId, { proof: proofImage });
      if (response.success) {
        // Êõ¥Êñ∞Êú¨Âú∞Áä∂ÊÄÅ
        setImagePreview(proofImage);
      } else {
        throw new Error(response.message || '‰∏ä‰º†Â§±Ë¥•');
      }
    } catch (error) {
      throw error;
    }
  };

  return (
    <div className={`border rounded-lg p-4 ${
      task.completed 
        ? 'bg-green-50 border-green-200' 
        : disabled 
          ? 'bg-gray-50 border-gray-200' 
          : 'bg-white border-gray-200'
    } ${disabled ? 'opacity-60' : ''}`}>
      <div className="flex items-start justify-between">
        <div className="flex items-start space-x-3 flex-1">
          <button
            onClick={handleStatusToggle}
            disabled={disabled}
            className={`mt-1 w-5 h-5 rounded border-2 flex items-center justify-center ${
              task.completed
                ? 'bg-green-500 border-green-500 text-white'
                : disabled
                  ? 'border-gray-300 cursor-not-allowed'
                  : 'border-gray-300 hover:border-blue-500'
            }`}
          >
            {task.completed && (
              <svg className="w-3 h-3" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
              </svg>
            )}
          </button>

          <div className="flex-1">
            <h3 className={`font-semibold ${
              task.completed ? 'text-green-700 line-through' : 'text-gray-800'
            }`}>
              {task.title || '‰ªªÂä°Ê†áÈ¢ò'}
            </h3>
            {task.description && (
              <p className="text-gray-600 text-sm mt-1">{task.description}</p>
            )}

            <div className="mt-2 flex items-center space-x-4 text-sm">
              <span className={`px-2 py-1 rounded text-xs ${
                task.type === 'Êï∞Â≠¶' 
                  ? 'bg-blue-100 text-blue-800'
                  : task.type === 'Ëã±ËØ≠'
                    ? 'bg-green-100 text-green-800'
                    : task.type === '‰∏ì‰∏öËØæ'
                      ? 'bg-purple-100 text-purple-800'
                      : task.type === '‰ºëÊÅØ'
                        ? 'bg-yellow-100 text-yellow-800'
                        : 'bg-gray-100 text-gray-800'
              }`}>
                {task.type || 'ÂÖ∂‰ªñ'}
              </span>

              {task.completed && task.completedAt && (
                <span className="text-green-600">
                  ÂÆåÊàê‰∫é: {new Date(task.completedAt).toLocaleTimeString('zh-CN')}
                </span>
              )}

              {imagePreview && (
                <span className="text-green-600 text-xs">üì∑ Â∑≤‰∏ä‰º†Âá≠ËØÅ</span>
              )}
            </div>

            {/* ÁÖßÁâá‰∏ä‰º†Âå∫Âüü */}
            {task.completed && (
              <div className="mt-3">
                {!showImageUpload ? (
                  <div className="flex items-center space-x-2">
                    {imagePreview ? (
                      <div className="flex items-center space-x-2">
                        <img 
                          src={imagePreview} 
                          alt="‰ªªÂä°Âá≠ËØÅ" 
                          className="w-16 h-16 object-cover rounded border cursor-pointer"
                          onClick={() => {
                            // ÁÇπÂáªÊü•ÁúãÂ§ßÂõæ
                            const newWindow = window.open();
                            newWindow.document.write(`<img src="${imagePreview}" style="max-width:100%; max-height:100%;" />`);
                          }}
                        />
                        <button
                          onClick={() => setShowImageUpload(true)}
                          className="text-blue-600 text-xs hover:text-blue-800"
                        >
                          Êõ¥Êç¢Âá≠ËØÅ
                        </button>
                      </div>
                    ) : (
                      <button
                        onClick={() => setShowImageUpload(true)}
                        className="px-3 py-1 bg-green-500 text-white rounded text-xs hover:bg-green-600"
                        disabled={disabled}
                      >
                        üì∑ ‰∏ä‰º†ÂÆåÊàêÂá≠ËØÅ
                      </button>
                    )}
                  </div>
                ) : (
                  <div className="border rounded p-3 bg-gray-50">
                    <div className="flex flex-col space-y-2">
                      <input
                        type="file"
                        accept="image/*"
                        onChange={handleImageSelect}
                        className="text-xs"
                      />
                      {selectedImage && (
                        <div className="flex items-center space-x-2">
                          <img 
                            src={imagePreview} 
                            alt="È¢ÑËßà" 
                            className="w-16 h-16 object-cover rounded border"
                          />
                          <div className="flex space-x-2">
                            <button
                              onClick={handleImageUpload}
                              className="px-3 py-1 bg-blue-500 text-white rounded text-xs hover:bg-blue-600"
                            >
                              Á°ÆËÆ§‰∏ä‰º†
                            </button>
                            <button
                              onClick={() => {
                                setShowImageUpload(false);
                                setSelectedImage(null);
                                setImagePreview(task.proof || null);
                              }}
                              className="px-3 py-1 bg-gray-500 text-white rounded text-xs hover:bg-gray-600"
                            >
                              ÂèñÊ∂à
                            </button>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>

        <div className="ml-4">
          {isEditing ? (
            <div className="flex items-center space-x-2">
              <select
                value={duration.hour}
                onChange={(e) => setDuration({...duration, hour: parseInt(e.target.value)})}
                className="w-16 px-2 py-1 border rounded text-sm bg-white"
              >
                {Array.from({length: 24}, (_, i) => (
                  <option key={i} value={i}>{i}</option>
                ))}
              </select>
              <span className="text-sm">Êó∂</span>
              <select
                value={duration.minute}
                onChange={(e) => setDuration({...duration, minute: parseInt(e.target.value)})}
                className="w-16 px-2 py-1 border rounded text-sm bg-white"
              >
                {Array.from({length: 12}, (_, i) => (
                  <option key={i * 5} value={i * 5}>{i * 5}</option>
                ))}
              </select>
              <span className="text-sm">ÂàÜ</span>
              <button
                onClick={handleDurationSave}
                className="px-3 py-1 bg-blue-500 text-white rounded text-sm hover:bg-blue-600"
              >
                ‰øùÂ≠ò
              </button>
              <button
                onClick={() => setIsEditing(false)}
                className="px-3 py-1 bg-gray-500 text-white rounded text-sm hover:bg-gray-600"
              >
                ÂèñÊ∂à
              </button>
            </div>
          ) : (
            <div className="text-right">
              <div className="text-sm text-gray-600 mb-1">
                Áî®Êó∂: {formatDuration()}
              </div>
              {!disabled && (
                <button
                  onClick={() => setIsEditing(true)}
                  className="text-xs text-blue-600 hover:text-blue-800"
                >
                  ÁºñËæëÊó∂Èïø
                </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TodayScreen;
