import React, { useState, useEffect } from 'react';
import { useApp } from '../contexts/AppContext';
import { taskAPI } from '../services/api';
import TodayScreen from './TodayScreen';
import MonthScreen from './MonthScreen';
import ProfileScreen from './ProfileScreen';

const StudentApp = () => {
  const { user, systemDate, setSystemDateSafely, advanceDay, resetToInitialDate, canSetDate, initialDate } = useApp();
  const [activeTab, setActiveTab] = useState('ÂΩìÊó•‰ªªÂä°');
  const [reminderShown, setReminderShown] = useState(false);
  const [alertVisible, setAlertVisible] = useState(false);

  // ÊâãÂä®Ëß¶Âèë24:00‰ªªÂä°Â§ÑÁêÜ
  const handleMidnightProcess = async () => {
    if (!user) return;

    const currentDate = systemDate.toISOString().split('T')[0];
    const confirmed = window.confirm(`Á°ÆÂÆöË¶ÅÊ®°Êãü24:00Ëá™Âä®Â§ÑÁêÜ ${currentDate} ÁöÑÊú™ÂÆåÊàê‰ªªÂä°ÂêóÔºü\n\nËøôÂ∞ÜÔºö\n‚Ä¢ Â§ÑÁêÜÂΩìÂ§©Êú™ÂÆåÊàêÁöÑ‰ªªÂä°\n‚Ä¢ Ê†πÊçÆÊï∞ÈáèËøõË°åÁªìËΩ¨ÊàñÈ°∫Âª∂\n‚Ä¢ ÂΩ±ÂìçÂêéÁª≠‰ªªÂä°ÂÆâÊéí`);

    if (!confirmed) return;

    try {
      const response = await fetch('/api/admin/reschedule-tasks', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          studentId: user.id,
          targetDate: currentDate
        })
      });

      const result = await response.json();

      if (result.success) {
        alert('24:00‰ªªÂä°Â§ÑÁêÜÂÆåÊàêÔºÅ\n\n' + JSON.stringify(result.data, null, 2));
        // Âà∑Êñ∞È°µÈù¢Êï∞ÊçÆ
        window.location.reload();
      } else {
        alert('Â§ÑÁêÜÂ§±Ë¥•: ' + result.message);
      }
    } catch (error) {
      console.error('24:00Â§ÑÁêÜÂ§±Ë¥•:', error);
      alert('Â§ÑÁêÜÂ§±Ë¥•: ' + error.message);
    }
  };

  // Ê£ÄÊü•‰ªäÊó•ÊòØÂê¶Êúâ‰ªªÂä°ÁöÑÊèêÈÜíÈÄªËæë
  useEffect(() => {
    const checkTodayTasks = async () => {
      if (user && !reminderShown) {
        try {
          const today = systemDate.toISOString().split('T')[0];
          const response = await taskAPI.getTasks(today, today);

          if (response.success && response.data) {
            // ÂêéÁ´ØËøîÂõûÁöÑÊòØÊåâÊó•ÊúüÂàÜÁªÑÁöÑÂØπË±°ÔºåÈúÄË¶ÅÊèêÂèñ‰ªäÂ§©ÁöÑ‰ªªÂä°
            const todayTasks = response.data[today] || [];

            if (todayTasks.length > 0) {
              // Ê£ÄÊü•ÊòØÂê¶ÊúâÈùû‰ºëÊÅØ‰ªªÂä°
              const hasActiveTasks = todayTasks.some(task => 
                task.type !== '‰ºëÊÅØ' && task.title !== '‰ªäÊó•‰ºëÊÅØË∞ÉÊï¥Áä∂ÊÄÅ'
              );

              if (hasActiveTasks) {
                setAlertVisible(true);
              }
            }
          }
        } catch (error) {
          console.error('Ê£ÄÊü•‰ªäÊó•‰ªªÂä°Â§±Ë¥•:', error);
        }
        setReminderShown(true);
      }
    };

    checkTodayTasks();
  }, [user, reminderShown, systemDate]);

  useEffect(() => {
    if (!user) {
      setReminderShown(false);
    }
  }, [user]);

  const NavItem = ({ name, icon }) => (
    <button
      onClick={() => setActiveTab(name)}
      className={`flex-1 flex flex-col items-center justify-center p-2 ${
        activeTab === name ? 'text-blue-500' : 'text-gray-500'
      }`}
    >
      <span className="text-xl mb-1">{icon}</span>
      <span className="text-xs">{name}</span>
    </button>
  );

  const ConfirmationModal = ({ visible, onClose, title, message }) => {
    if (!visible) return null;

    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
        <div className="bg-white rounded-lg shadow-xl p-6 w-11/12 max-w-sm">
          <h3 className="text-lg font-bold">{title}</h3>
          <p className="text-gray-600 my-4">{message}</p>
          <div className="flex justify-end">
            <button
              onClick={onClose}
              className="px-4 py-2 bg-blue-600 text-white rounded"
            >
              Á°ÆËÆ§
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="w-full min-h-screen flex flex-col bg-gray-50">
      <ConfirmationModal
        visible={alertVisible}
        onClose={() => setAlertVisible(false)}
        title="ÊØèÊó•ÊèêÈÜí"
        message="ÂêåÂ≠¶‰Ω†Â•ΩÔºå‰ªäÂ§©ÁöÑÂ≠¶‰π†‰ªªÂä°Â∑≤ÁªèÂèëÂ∏ÉÔºåËØ∑ËÆ∞ÂæóÊåâÊó∂ÂÆåÊàêÂì¶ÔºÅ"
      />

      {/* Êó•ÊúüÊéßÂà∂Âô® - ‰ªÖÁî®‰∫éÊµãËØï */}
      <div className="bg-yellow-100 p-2 text-center text-xs border-b">
        <div className="flex items-center justify-center space-x-2 flex-wrap">
          <span className="mr-2">üß™ ÊµãËØïÊó•ÊúüÊéßÂà∂: </span>

          {/* ÂΩìÂâçÊó•ÊúüÊòæÁ§∫ */}
          <span className="text-blue-700 font-semibold">
            ÂΩìÂâç: {systemDate.toLocaleDateString('zh-CN')}
          </span>

          {/* ÂâçËøõ‰∏ÄÂ§©ÊåâÈíÆ */}
          <button 
            onClick={advanceDay}
            className="mx-1 px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
          >
            +1Â§©
          </button>

          {/* Âø´ÈÄüË∑≥ËΩ¨Âà∞Êú™Êù•Êó•ÊúüÁöÑÊåâÈíÆÔºàÂè™ÊòæÁ§∫Êú™Êù•Êó•ÊúüÔºâ */}
          {canSetDate(new Date('2025-07-15')) && (
            <button 
              onClick={() => setSystemDateSafely(new Date('2025-07-15'))}
              className="mx-1 px-2 py-1 bg-green-500 text-white rounded hover:bg-green-600 transition-colors"
            >
              7/15
            </button>
          )}

          {canSetDate(new Date('2025-07-16')) && (
            <button 
              onClick={() => setSystemDateSafely(new Date('2025-07-16'))}
              className="mx-1 px-2 py-1 bg-purple-500 text-white rounded hover:bg-purple-600 transition-colors"
            >
              7/16
            </button>
          )}

          {/* Ê®°Êãü24:00Â§ÑÁêÜÊåâÈíÆ */}
          <button 
            onClick={handleMidnightProcess}
            className="mx-1 px-3 py-1 bg-orange-500 text-white rounded hover:bg-orange-600 transition-colors"
            title="ÊâãÂä®Ëß¶Âèë24:00Ëá™Âä®‰ªªÂä°Â§ÑÁêÜ"
          >
            24:00Â§ÑÁêÜ
          </button>

          {/* ÈáçÁΩÆÊåâÈíÆ */}
          <button 
            onClick={resetToInitialDate}
            className="mx-1 px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600 transition-colors"
          >
            ÈáçÁΩÆ
          </button>
        </div>

        {/* ÊèêÁ§∫‰ø°ÊÅØ */}
        <div className="mt-1 text-gray-600">
          Âè™ËÉΩÂâçËøõÂà∞Êú™Êù•Êó•ÊúüÔºåÈáçÁΩÆÂõûÂà∞ÂàùÂßãÁä∂ÊÄÅ ({initialDate.toLocaleDateString('zh-CN')})
        </div>
      </div>

      <main className="flex-1 overflow-hidden flex flex-col bg-white">
        {activeTab === 'ÂΩìÊó•‰ªªÂä°' && <TodayScreen />}
        {activeTab === 'ÊúàÂ∫¶‰ªªÂä°' && <MonthScreen />}
        {activeTab === '‰∏™‰∫∫‰∏≠ÂøÉ' && <ProfileScreen />}
      </main>

      <nav className="flex bg-white border-t shadow-lg md:shadow-md sticky bottom-0">
        <NavItem name="ÂΩìÊó•‰ªªÂä°" icon="üìÖ" />
        <NavItem name="ÊúàÂ∫¶‰ªªÂä°" icon="üóìÔ∏è" />
        <NavItem name="‰∏™‰∫∫‰∏≠ÂøÉ" icon="üë§" />
      </nav>
    </div>
  );
};

export default StudentApp;
